# -*- coding:utf-8 -*-

# 给定两个字符串 s1 和 s2，写一个函数来判断 s2 是否包含 s1 的排列。
# 换句话说，第一个字符串的排列之一是第二个字符串的子串。

from collections import Counter
import time


class Solution(object):

    def checkInclusion(self, s1, s2):
        """
        :type s1: str
        :type s2: str
        :rtype: bool
        """
        s1_len = len(s1)
        s2_len = len(s2)
        if s1_len > s2_len:
            return False

        s1_dict = dict(Counter(s1))

        diff = s2_len - s1_len

        for i in range(0, diff + 1):
            result = s2[i: (i + s1_len)]
            result_dict = dict(Counter(result))
            if result_dict == s1_dict:
                return True
        return False


if __name__ == '__main__':
    s = Solution()
    # s1 = "su"
    # s2 = "byaudlsutooqd"

    # s1 = ""
    # s2 = ""
    # s1 = 'aaaabbbccc'
    # s2 = 'acaaaaaaccaaaaabcbb'

    # s1 = "ky"
    # s2 = "ainwkckifykxlribaypk"

    # s1 = "trfhcbogglim"
    # s2 = ""
    s1 = 'a'
    s2 = 'b'

    start = time.clock()
    print(s.checkInclusion(s1, s2))
    end = time.clock()
    print(end - start)



